import React, { useState } from 'react';
import './App.css';

function App() {
  const [eventType, setEventType] = useState('100m');
  const [performance, setPerformance] = useState('');
  const [points, setPoints] = useState(null);
  const [loading, setLoading] = useState(false);

  // Event categories and their events
  const events = {
    'Sprint Events': ['100m', '200m', '400m'],
    'Middle Distance': ['800m', '1500m', '3000m'],
    'Long Distance': ['5000m', '10000m', 'Marathon'],
    'Hurdles': ['110mH', '400mH', '3000mSC'],
    'Jumps': ['High Jump', 'Pole Vault', 'Long Jump', 'Triple Jump'],
    'Throws': ['Shot Put', 'Discus', 'Hammer', 'Javelin']
  };

  const calculatePoints = async () => {
    if (!performance) return;

    setLoading(true);
    try {
      const response = await fetch('http://localhost:5001/api/calculate-points', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          event_type: eventType, 
          performance: parseFloat(performance) 
        }),
      });
      const data = await response.json();
      setPoints(Math.round(data.points));
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to calculate points. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    calculatePoints();
  };

  const getPlaceholder = () => {
    if (eventType.includes('Jump')) return 'Enter height/distance (m)';
    if (eventType.includes('Put') || eventType.includes('Throw') || 
        eventType.includes('Discus') || eventType.includes('Hammer')) return 'Enter distance (m)';
    return 'Enter time (seconds)';
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>IAAF Points Calculator</h1>
        <form onSubmit={handleSubmit} className="calculator-form">
          <div className="event-selection">
            <label htmlFor="event-select">Select Event:</label>
            <select 
              id="event-select"
              value={eventType} 
              onChange={(e) => setEventType(e.target.value)}
              className="event-dropdown"
            >
              {Object.entries(events).map(([category, categoryEvents]) => (
                <optgroup label={category} key={category}>
                  {categoryEvents.map(event => (
                    <option key={event} value={event}>{event}</option>
                  ))}
                </optgroup>
              ))}
            </select>
          </div>

          <div className="performance-input">
            <label htmlFor="performance-input">Performance:</label>
            <input
              id="performance-input"
              type="number"
              step="0.01"
              value={performance}
              onChange={(e) => setPerformance(e.target.value)}
              placeholder={getPlaceholder()}
              className="performance-field"
            />
          </div>

          <button 
            type="submit" 
            className="calculate-button"
            disabled={loading || !performance}
          >
            {loading ? 'Calculating...' : 'Calculate Points'}
          </button>
        </form>

        {points !== null && (
          <div className="results">
            <h2>Results</h2>
            <p className="points">Points: {points}</p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
