export const WIND_AFFECTED_EVENTS = [
  '100m', '200m', '100mH', '110mH', 'LJ', 'TJ'
];

export const calculateWindModification = (windSpeed, hasWindInfo = true) => {
  // Handle NWI (No Wind Information) case
  if (!hasWindInfo || windSpeed === '' || windSpeed === null) {
    return -30;
  }

  // Convert to number and handle invalid inputs
  const wind = Number(windSpeed);
  if (isNaN(wind)) {
    return 0;
  }

  // For tailwind (positive values)
  if (wind >= 0) {
    if (wind <= 2.0) {
      return 0;  // No modification for tailwind up to +2.0 m/s
    } else {
      // For winds above 2.1, calculate deduction
      // Starting from 0.0 m/s at 6 points per m/s
      return -(wind * 6);  // This gives -24 for +4.0, -18 for +3.0, etc.
    }
  }

  // For headwind (negative values)
  // Simple multiplication: -4.0 gives +24, -3.0 gives +18, etc.
  return Math.abs(wind) * 6;
};

// Reference table matching exactly with the specification
export const WIND_REFERENCE = {
  '+4.0': -24,  // Tail wind
  '+3.0': -18,
  '+2.0': 0,
  '+1.0': 0,
  '0.0': 0,
  '-1.0': 6,    // Head wind
  '-2.0': 12,
  '-3.0': 18,
  '-4.0': 24
};
