export const WIND_AFFECTED_EVENTS = [
  '100m', '200m', '100mH', '110mH', 'LJ', 'TJ'
];

export const calculateWindModification = (windSpeed, hasWindInfo = true) => {
  // Handle NWI (No Wind Information) case
  if (!hasWindInfo) return -30;

  // Convert to number if string
  windSpeed = Number(windSpeed);

  // For tailwind (positive values)
  if (windSpeed >= 0) {
    if (windSpeed <= 2.0) {
      return 0;  // No modification for tailwind up to +2.0 m/s
    } else {
      // Calculate deduction for tailwind above 2.0 m/s
      // 6 points per m/s starting from 0
      return -(windSpeed * 6);
    }
  }

  // For headwind (negative values)
  // Add points (6 points per m/s)
  return Math.abs(windSpeed) * 6;
};

// Reference table matching the official specification
export const WIND_REFERENCE_TABLE = {
  '+4.0': -24,
  '+3.0': -18,
  '+2.0': 0,
  '+1.0': 0,
  '0.0': 0,
  '-1.0': 6,
  '-2.0': 12,
  '-3.0': 18,
  '-4.0': 24
};
