import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [eventType, setEventType] = useState('100m');
  const [performance, setPerformance] = useState('');
  const [points, setPoints] = useState(null);
  const [season, setSeason] = useState('outdoor');
  const [loading, setLoading] = useState(false);
  const [mode, setMode] = useState('performance'); // 'performance' or 'points' mode
  const [equivalentResults, setEquivalentResults] = useState(null);
  const [gender, setGender] = useState('mens');

  // Separate events for indoor and outdoor
  const events = {
    outdoor: {
      'Sprint Events': ['100m', '200m', '300m', '400m', '500m'],
      'Middle Distance': ['600m', '800m', '1000m', '1500m', 'Mile', '2000m', '3000m'],
      'Long Distance': ['2Miles', '5000m', '10000m', '5km', '10km', '15km', '10Miles', '20km', 'HalfMarathon', '25km', '30km', 'Marathon', '100km'],
      'Hurdles': ['110mH', '400mH'],
      'Steeplechase': ['2000mSC', '3000mSC'],
      'Jumps': ['HJ', 'PV', 'LJ', 'TJ'],
      'Throws': ['SP', 'DT', 'HT', 'JT'],
      'Combined': ['Decathlon']
    },
    indoor: {
      'Sprint Events': ['50m', '55m', '60m', '200m', '300m', '400m', '500m'],
      'Hurdles': ['50mH', '55mH', '60mH'],
      'Middle Distance': ['600m', '800m', '1000m', '1500m', 'Mile', '2000m', '3000m'],
      'Long Distance': ['2Miles', '5000m'],
      'Jumps': ['HJ', 'PV', 'LJ', 'TJ'],
      'Throws': ['SP'],
      'Combined': ['Heptathlon']
    }
  };

  // Reset event type when season changes
  useEffect(() => {
    const availableEvents = Object.values(events[season]).flat();
    if (!availableEvents.includes(eventType)) {
      setEventType(availableEvents[0]);
    }
  }, [season]);

  const calculateEquivalents = async (pointsValue) => {
    if (!pointsValue) return;

    const results = {};
    for (const categoryEvents of Object.values(events[season])) {
      for (const event of categoryEvents) {
        try {
          const response = await fetch('http://localhost:5001/api/calculate-performance', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ 
              event_type: event,
              points: pointsValue,
              season: season
            }),
          });

          const data = await response.json();
          if (!data.error) {
            results[event] = data.performance;
          }
        } catch (error) {
          console.error(`Error calculating for ${event}:`, error);
        }
      }
    }
    setEquivalentResults(results);
  };

  const calculate = async () => {
    if (mode === 'performance' && !performance) return;
    if (mode === 'points' && !points) return;

    setLoading(true);
    try {
      const endpoint = mode === 'performance' ? 'calculate-points' : 'calculate-performance';
      const value = mode === 'performance' ? parseFloat(performance) : parseInt(points);

      const response = await fetch(`http://localhost:5001/api/${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          event_type: eventType,
          [mode]: value,
          season: season,
          gender: gender
        }),
      });

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error);
      }

      if (mode === 'performance') {
        if (data.points >= 1400) {
          setPoints('Value out of range');
        } else {
          setPoints(Math.round(data.points));
        }
      } else {
        setPerformance(data.performance.toString());
      }

      // After successful calculation, get equivalents
      if (data.points && data.points < 1400) {
        await calculateEquivalents(data.points);
      }
    } catch (error) {
      console.error('Detailed error:', error);
      if (mode === 'performance') {
        setPoints('Value out of range');
      } else {
        setPerformance('Invalid points');
      }
    } finally {
      setLoading(false);
    }
  };

  const getPlaceholder = () => {
    if (eventType.includes('Jump')) return 'Enter height/distance (m)';
    if (eventType.includes('Vault')) return 'Enter height/distance (m)';
    if (eventType.includes('Put') || eventType.includes('Throw') || 
        eventType.includes('Discus') || eventType.includes('Hammer')) return 'Enter distance (m)';
    return 'Enter time (seconds)';
  };

  const formatPerformance = (value, event) => {
    // Events that need time formatting (longer than 400m)
    const timeEvents = ['600m', '800m', '1000m', '1500m', 'Mile', '2000m', '3000m', 
                       '2Miles', '5000m', '10000m', '5km', '10km', '15km', '10Miles', 
                       '20km', 'HalfMarathon', '25km', '30km', 'Marathon', '100km',
                       '2000mSC', '3000mSC'];

    if (!timeEvents.includes(event)) {
      return value;
    }

    // Convert to seconds
    const totalSeconds = parseFloat(value);

    // Calculate hours, minutes, seconds, and milliseconds
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = Math.floor(totalSeconds % 60);
    const milliseconds = Math.round((totalSeconds % 1) * 100);

    // Format based on duration
    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}.${milliseconds.toString().padStart(2, '0')}`;
    } else if (minutes > 0) {
      return `${minutes}:${seconds.toString().padStart(2, '0')}.${milliseconds.toString().padStart(2, '0')}`;
    } else {
      return `${seconds}.${milliseconds.toString().padStart(2, '0')}`;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>IAAF Points Calculator</h1>
        <form className="calculator-form" onSubmit={(e) => { e.preventDefault(); calculate(); }}>
          <div className="gender-toggle">
            <div className="toggle-container">
              <div 
                className={`toggle-slider ${gender === 'womens' ? 'right' : 'left'}`}
              />
              <div 
                className={`toggle-option ${gender === 'mens' ? 'active' : ''}`}
                onClick={() => {
                  setGender('mens');
                  setPoints(null);
                  setPerformance('');
                  setEquivalentResults(null);
                }}
              >
                Men
              </div>
              <div 
                className={`toggle-option ${gender === 'womens' ? 'active' : ''}`}
                onClick={() => {
                  setGender('womens');
                  setPoints(null);
                  setPerformance('');
                  setEquivalentResults(null);
                }}
              >
                Women
              </div>
            </div>
          </div>

          <div className="season-toggle">
            <div className="toggle-container">
              <div 
                className={`toggle-slider ${season === 'indoor' ? 'right' : 'left'}`}
              />
              <div 
                className={`toggle-option ${season === 'outdoor' ? 'active' : ''}`}
                onClick={() => setSeason('outdoor')}
              >
                Outdoor
              </div>
              <div 
                className={`toggle-option ${season === 'indoor' ? 'active' : ''}`}
                onClick={() => setSeason('indoor')}
              >
                Indoor
              </div>
            </div>
          </div>

          <div className="mode-toggle">
            <div className="toggle-container">
              <div 
                className={`toggle-slider ${mode === 'points' ? 'right' : 'left'}`}
              />
              <div 
                className={`toggle-option ${mode === 'performance' ? 'active' : ''}`}
                onClick={() => {
                  setMode('performance');
                  setPoints(null);
                  setPerformance('');
                }}
              >
                Performance
              </div>
              <div 
                className={`toggle-option ${mode === 'points' ? 'active' : ''}`}
                onClick={() => {
                  setMode('points');
                  setPoints(null);
                  setPerformance('');
                }}
              >
                Points
              </div>
            </div>
          </div>

          <div className="event-selection">
            <label htmlFor="event-select">Event:</label>
            <select
              id="event-select"
              value={eventType}
              onChange={(e) => setEventType(e.target.value)}
              className="event-dropdown"
            >
              {Object.entries(events[season]).map(([category, categoryEvents]) => (
                <optgroup key={category} label={category}>
                  {categoryEvents.map(event => (
                    <option key={event} value={event}>{event}</option>
                  ))}
                </optgroup>
              ))}
            </select>
          </div>

          <div className="performance-input">
            <label htmlFor="value-input">
              {mode === 'performance' ? 'Performance:' : 'Points:'}
            </label>
            <input
              id="value-input"
              type="number"
              step="0.01"
              value={mode === 'performance' ? performance : points}
              onChange={(e) => {
                if (mode === 'performance') {
                  setPerformance(e.target.value);
                } else {
                  setPoints(e.target.value);
                }
              }}
              className="performance-field"
              placeholder={mode === 'performance' ? "Enter performance..." : "Enter points (0-1400)..."}
            />
          </div>

          <button 
            type="submit" 
            className="calculate-button"
            disabled={loading}
          >
            {loading ? 'Calculating...' : 'Calculate'}
          </button>
        </form>

        {((mode === 'performance' && points !== null) || 
          (mode === 'points' && performance !== '')) && (
          <div className="results">
            <h2>Results</h2>
            <p className="points">
              {mode === 'performance' 
                ? (typeof points === 'number' ? `Points: ${points}` : points)
                : `Performance: ${performance}`}
            </p>
          </div>
        )}

        {equivalentResults && (
          <div className="comparison-table">
            <h2>Equivalent Performances</h2>
            <div className="table-container">
              {Object.entries(events[season]).map(([category, categoryEvents]) => (
                <div key={category} className="category-section">
                  <h3>{category}</h3>
                  <table>
                    <tbody>
                      {categoryEvents.map(event => (
                        <tr key={event}>
                          <td>{event}</td>
                          <td>{equivalentResults[event] ? formatPerformance(equivalentResults[event], event) : '-'}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ))}
            </div>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
