import { useState } from 'react';

function About() {
  const [expandedSections, setExpandedSections] = useState({});

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionId]: !prev[sectionId]
    }));
  };

  return (
    <div className="about-page">
      <section className={`about-section ${expandedSections['calculator'] ? 'expanded' : ''}`}>
        <h2 onClick={() => toggleSection('calculator')}>Points Calculator</h2>
        <div className="section-content">
          <p>The World Athletics Points Score is used to compare performances across different events...</p>
          {/* Rest of calculator content */}
        </div>
      </section>

      <section className={`about-section ${expandedSections['competition'] ? 'expanded' : ''}`}>
        <h2 onClick={() => toggleSection('competition')}>Competition Categories</h2>
        <div className="section-content">
          {/* Competition categories content */}
        </div>
      </section>

      {/* Other sections following the same pattern */}
    </div>
  );
}

export default About;
