import React, { useState } from 'react';
import { WIND_AFFECTED_EVENTS, calculateWindModification } from '../utils/windModification';
import CalculatorForm from './CalculatorForm';

function Calculator({ isWorldRankings = false }) {
  const [gender, setGender] = useState('mens');
  const [season, setSeason] = useState('outdoor');
  const [mode, setMode] = useState('result');
  const [selectedEvent, setSelectedEvent] = useState('100m');
  const [result, setResult] = useState('');
  const [windSpeed, setWindSpeed] = useState('');
  const [points, setPoints] = useState(null);

  const handleCalculate = async (formData) => {
    try {
      const response = await fetch('/api/calculate-points', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const data = await response.json();
      const basePoints = data.points;

      let windAdjustedPoints = basePoints;
      if (WIND_AFFECTED_EVENTS.includes(formData.event_type) && formData.windSpeed) {
        const windModification = calculateWindModification(formData.windSpeed);
        windAdjustedPoints = Math.max(0, Math.min(1400, basePoints + windModification));
      }

      setPoints({
        base: basePoints,
        windAdjusted: windAdjustedPoints
      });

    } catch (error) {
      console.error('Calculation error:', error);
      setPoints(null);
    }
  };

  return (
    <div className="calculator">
      <CalculatorForm
        gender={gender}
        setGender={setGender}
        season={season}
        setSeason={setSeason}
        mode={mode}
        setMode={setMode}
        selectedEvent={selectedEvent}
        setSelectedEvent={setSelectedEvent}
        result={result}
        setResult={setResult}
        windSpeed={windSpeed}
        setWindSpeed={setWindSpeed}
        onCalculate={handleCalculate}
        points={points}
        isWorldRankings={isWorldRankings}
      />
    </div>
  );
}

export default Calculator;
