import { useState, useEffect } from 'react';

function EventComparison({ points, gender, season }) {
  const [equivalentPerformances, setEquivalentPerformances] = useState({});

  useEffect(() => {
    if (points) {
      calculateEquivalentPerformances();
    }
  }, [points, gender, season]);

  const calculateEquivalentPerformances = async () => {
    let combinedEvent;
    if (season === 'outdoor') {
      combinedEvent = gender === 'mens' ? 'DEC' : 'HEP';
    } else {
      combinedEvent = gender === 'mens' ? 'HEP' : 'PEN';
    }

    try {
      const response = await fetch('http://localhost:5001/api/calculate-performance', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          event_type: combinedEvent,
          points: points,
          gender: gender,
          season: season
        }),
      });

      const data = await response.json();
      if (data.performance) {
        setEquivalentPerformances(prev => ({
          ...prev,
          [combinedEvent]: data.performance
        }));
      }
    } catch (error) {
      console.error('Error calculating combined event:', error);
    }

    // Calculate other events
    const regularEvents = season === 'indoor' 
      ? [
          '60m', '200m', '400m', '800m', '1500m', '3000m',
          '60mH',
          'HJ', 'PV', 'LJ', 'TJ',
          'SP'
        ]
      : [
          '100m', '200m', '400m', '800m', '1500m', '3000m', '5000m', '10000m',
          gender === 'mens' ? '110mH' : '100mH', '400mH', '3000mSC',
          'HJ', 'PV', 'LJ', 'TJ',
          'SP', 'DT', 'HT', 'JT'
        ];

    for (const event of regularEvents) {
      try {
        const response = await fetch('http://localhost:5001/api/calculate-performance', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            event_type: event,
            points: points,
            gender: gender,
            season: season
          }),
        });

        const data = await response.json();
        if (data.performance) {
          setEquivalentPerformances(prev => ({
            ...prev,
            [event]: data.performance
          }));
        }
      } catch (error) {
        console.error(`Error calculating ${event}:`, error);
      }
    }
  };

  const getEventName = (event) => {
    const eventNames = {
      'DEC': 'Decathlon',
      'HEP': 'Heptathlon',
      'PEN': 'Pentathlon'
    };
    return eventNames[event] || event;
  };

  const renderEventSection = (title, events) => {
    const filteredEvents = events.filter(event => equivalentPerformances[event] !== undefined);
    if (filteredEvents.length === 0) return null;

    return (
      <div className="event-section">
        <h3>{title}</h3>
        <table>
          <tbody>
            {filteredEvents.map(event => (
              <tr key={event}>
                <td>{getEventName(event)}</td>
                <td>{equivalentPerformances[event] || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="event-comparison">
      <h2>Required Performances for {points} Points</h2>
      {renderEventSection('Sprints & Hurdles', 
        season === 'indoor' 
          ? ['60m', '200m', '400m', '60mH']
          : ['100m', '200m', '400m', gender === 'mens' ? '110mH' : '100mH', '400mH']
      )}
      {renderEventSection('Middle Distance', ['800m', '1500m', '3000m'])}
      {season === 'outdoor' && renderEventSection('Long Distance', ['5000m', '10000m'])}
      {renderEventSection('Jumps', ['HJ', 'PV', 'LJ', 'TJ'])}
      {renderEventSection('Throws', 
        season === 'indoor' 
          ? ['SP']
          : ['SP', 'DT', 'HT', 'JT']
      )}
      {renderEventSection('Combined Events', [
        season === 'outdoor'
          ? (gender === 'mens' ? 'DEC' : 'HEP')
          : (gender === 'mens' ? 'HEP' : 'PEN')
      ])}
    </div>
  );
}

export default EventComparison;
