import { useState } from 'react';
import Navbar from './components/Navbar';
import CompetitionTable from './CompetitionTable';
import About from './components/About';
import EventComparison from './components/EventComparison';
import { calculateWindModification, needsWindInput } from './utils/windModification';
import { isCombinedEvent } from './utils/eventCodes';
import { formatTimeInput } from './utils/formatters';
import WindAdjustment from './components/CalculatorForm/WindAdjustment';
import Form from './components/CalculatorForm/Form';
import ResultsDisplay from './components/CalculatorForm/ResultsDisplay';

function App() {
  const [activeTab, setActiveTab] = useState('calculator');
  const [mode, setMode] = useState('points');
  const [gender, setGender] = useState('mens');
  const [season, setSeason] = useState('outdoor');
  const [eventType, setEventType] = useState('100m');
  const [performance, setPerformance] = useState('');
  const [points, setPoints] = useState('');
  const [windSpeed, setWindSpeed] = useState('');
  const [showWind, setShowWind] = useState(false);
  const [adjustedPoints, setAdjustedPoints] = useState(null);

  const calculate = () => {
    if (mode === 'points') {
      let formattedPerformance = performance;
      if (['800m', '1500m', '3000m', '5000m', '10000m'].includes(eventType)) {
        formattedPerformance = formatTimeInput(performance);
      }

      fetch(`/api/calculate/${gender}/${season}/${eventType}/${formattedPerformance}`)
        .then(response => response.json())
        .then(data => {
          setPoints(data.points);

          if (needsWindInput(eventType) && showWind && windSpeed) {
            const windModification = calculateWindModification(eventType, windSpeed);
            setAdjustedPoints(Math.max(0, parseInt(data.points) + windModification));
          } else {
            setAdjustedPoints(data.points);
          }
        })
        .catch(error => {
          console.error('Error:', error);
          setPoints('');
          setAdjustedPoints(null);
        });
    } else {
      calculatePerformance();
    }
  };

  const calculatePerformance = () => {
    if (!points) return;

    fetch(`/api/performance/${gender}/${season}/${eventType}/${points}`)
      .then(response => response.json())
      .then(data => {
        if (isCombinedEvent(eventType)) {
          setPerformance(Math.round(data.performance));
        } else {
          setPerformance(data.performance);
        }
      })
      .catch(error => {
        console.error('Error:', error);
        setPerformance('');
      });
  };

  return (
    <div className="App">
      <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
      <main className="main-content">
        {activeTab === 'calculator' ? (
          <div className="content-container">
            <div className="calculator-wrapper">
              <div className="calculator-form">
                <Form
                  mode={mode}
                  setMode={setMode}
                  gender={gender}
                  setGender={setGender}
                  season={season}
                  setSeason={setSeason}
                  eventType={eventType}
                  setEventType={setEventType}
                  performance={performance}
                  setPerformance={setPerformance}
                  points={points}
                  setPoints={setPoints}
                />

                <WindAdjustment
                  eventType={eventType}
                  windSpeed={windSpeed}
                  setWindSpeed={setWindSpeed}
                  showWind={showWind}
                  setShowWind={setShowWind}
                  needsWindInput={needsWindInput}
                />

                <button className="calculate-button" onClick={calculate}>
                  Calculate
                </button>

                <ResultsDisplay
                  mode={mode}
                  points={points}
                  performance={performance}
                  eventType={eventType}
                  windSpeed={windSpeed}
                  adjustedPoints={adjustedPoints}
                />
              </div>
            </div>

            <div className="comparison-wrapper">
              <EventComparison 
                points={adjustedPoints || points}
                gender={gender}
                season={season}
              />
            </div>
          </div>
        ) : activeTab === 'competition' ? (
          <CompetitionTable 
            points={adjustedPoints || points}
            eventType={eventType}
            onCalculatePerformance={calculatePerformance}
          />
        ) : (
          <About />
        )}
      </main>
    </div>
  );
}

export default App;
