import { useState, useEffect } from 'react';

function CompetitionTable({ points }) {
  const [equivalentPerformances, setEquivalentPerformances] = useState({});

  useEffect(() => {
    if (points) {
      calculateEquivalentPerformances();
    }
  }, [points]);

  const calculateEquivalentPerformances = async () => {
    const events = [
      // Track Events
      '100m', '200m', '400m', '800m', '1500m', '3000m', '5000m', '10000m',
      // Hurdles
      '100mH', '110mH', '400mH', '3000mSC',
      // Jumps
      'HJ', 'PV', 'LJ', 'TJ',
      // Throws
      'SP', 'DT', 'HT', 'JT'
    ];

    const performances = {};

    for (const event of events) {
      try {
        const response = await fetch('http://localhost:5001/api/calculate-performance', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            event_type: event,
            points: points,
            gender: 'mens', // You might want to make this dynamic
            season: 'outdoor' // You might want to make this dynamic
          }),
        });
        const data = await response.json();
        performances[event] = data.performance;
      } catch (error) {
        console.error(`Error calculating performance for ${event}:`, error);
        performances[event] = 'N/A';
      }
    }

    setEquivalentPerformances(performances);
  };

  const renderEventSection = (title, events) => (
    <div className="event-section">
      <h3>{title}</h3>
      <table>
        <tbody>
          {events.map(event => (
            <tr key={event}>
              <td>{event}</td>
              <td>{equivalentPerformances[event] || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  if (!points) {
    return (
      <div className="comparison-table">
        <p>Enter points to see equivalent performances</p>
      </div>
    );
  }

  return (
    <div className="comparison-table">
      <h2>Equivalent Performances for {points} Points</h2>

      {renderEventSection('Sprints & Hurdles', ['100m', '200m', '400m', '100mH', '110mH', '400mH'])}
      {renderEventSection('Middle Distance', ['800m', '1500m', '3000m', '3000mSC'])}
      {renderEventSection('Long Distance', ['5000m', '10000m'])}
      {renderEventSection('Jumps', ['HJ', 'PV', 'LJ', 'TJ'])}
      {renderEventSection('Throws', ['SP', 'DT', 'HT', 'JT'])}
    </div>
  );
}

export default CompetitionTable;
