import './App.css';
import { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import CompetitionTable from './CompetitionTable';
import About from './components/About';
import EventComparison from './components/EventComparison';
import { WIND_AFFECTED_EVENTS, getWindAdjustedScore } from './utils/windModification';
import CalculatorForm from './components/CalculatorForm';

function App() {
  // Shared state for both calculators
  const [gender, setGender] = useState('mens');
  const [season, setSeason] = useState('outdoor');
  const [mode, setMode] = useState('result');
  const [selectedEvent, setSelectedEvent] = useState('100m');
  const [result, setResult] = useState('');
  const [windSpeed, setWindSpeed] = useState('');
  const [points, setPoints] = useState(null);
  const [activeTab, setActiveTab] = useState('calculator');

  const handleCalculate = async () => {
    try {
      if (!result) {
        return; // Don't calculate if no result entered
      }

      if (mode === 'result') {
        const response = await fetch('http://localhost:5000/api/calculate-points', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            event_type: selectedEvent,
            performance: parseFloat(result),
            gender: gender,
            season: season,
          }),
        });

        const data = await response.json();
        if (data.points) {
          setPoints(data.points);
        }
      } else {
        const response = await fetch('http://localhost:5000/api/calculate-performance', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            event_type: selectedEvent,
            points: parseInt(result),
            gender: gender,
            season: season,
          }),
        });

        const data = await response.json();
        if (data.performance) {
          setResult(data.performance.toString());
        }
      }
    } catch (error) {
      console.error('Calculation error:', error);
      // Optionally add error handling UI feedback here
    }
  };

  // Add this effect to auto-calculate when inputs change
  useEffect(() => {
    if (result) {
      handleCalculate();
    }
  }, [result, selectedEvent, gender, season, mode]);

  return (
    <div className="App">
      <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
      <main className="main-content">
        {activeTab === 'calculator' ? (
          <div className="content-container">
            <div className="calculator-wrapper">
              <CalculatorForm
                gender={gender}
                setGender={setGender}
                season={season}
                setSeason={setSeason}
                mode={mode}
                setMode={setMode}
                selectedEvent={selectedEvent}
                setSelectedEvent={setSelectedEvent}
                result={result}
                setResult={setResult}
                windSpeed={windSpeed}
                setWindSpeed={setWindSpeed}
                onCalculate={handleCalculate}
                points={points}
              />
            </div>
          </div>
        ) : activeTab === 'competition' ? (
          <div className="content-container">
            <div className="competition-wrapper">
              <div className="calculator-section">
                <CalculatorForm
                  gender={gender}
                  setGender={setGender}
                  season={season}
                  setSeason={setSeason}
                  mode={mode}
                  setMode={setMode}
                  selectedEvent={selectedEvent}
                  setSelectedEvent={setSelectedEvent}
                  result={result}
                  setResult={setResult}
                  windSpeed={windSpeed}
                  setWindSpeed={setWindSpeed}
                  onCalculate={handleCalculate}
                  points={points}
                  isWorldRankings={true}
                />
              </div>
              <CompetitionTable 
                points={points}
                eventType={selectedEvent}
                onCalculatePerformance={handleCalculate}
              />
            </div>
          </div>
        ) : (
          <About />
        )}
      </main>
    </div>
  );
}

export default App;
