import { useState, useEffect } from 'react';

function EventComparison({ points, gender, season }) {
  const [equivalentPerformances, setEquivalentPerformances] = useState({});

  useEffect(() => {
    if (points) {
      calculateEquivalentPerformances();
    }
  }, [points, gender, season]);

  const calculateEquivalentPerformances = async () => {
    // Define all events including combined events
    const allEvents = season === 'indoor' 
      ? {
          'mens': [
            '60m', '200m', '400m', '800m', '1500m', '3000m',
            '60mH',
            'HJ', 'PV', 'LJ', 'TJ',
            'SP',
            'HEP'  // Indoor men's heptathlon
          ],
          'womens': [
            '60m', '200m', '400m', '800m', '1500m', '3000m',
            '60mH',
            'HJ', 'PV', 'LJ', 'TJ',
            'SP',
            'PEN'  // Indoor women's pentathlon
          ]
        }
      : {
          'mens': [
            '100m', '200m', '400m', '800m', '1500m', '3000m', '5000m', '10000m',
            '110mH', '400mH', '3000mSC',
            'HJ', 'PV', 'LJ', 'TJ',
            'SP', 'DT', 'HT', 'JT',
            'DEC'  // Outdoor men's decathlon
          ],
          'womens': [
            '100m', '200m', '400m', '800m', '1500m', '3000m', '5000m', '10000m',
            '100mH', '400mH', '3000mSC',
            'HJ', 'PV', 'LJ', 'TJ',
            'SP', 'DT', 'HT', 'JT',
            'HEP'  // Outdoor women's heptathlon
          ]
        };

    const events = allEvents[gender];
    const performances = {};

    for (const event of events) {
      try {
        const response = await fetch('http://localhost:5001/api/calculate-performance', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            event_type: event,
            points: points,
            gender: gender,
            season: season
          }),
        });

        const data = await response.json();
        if (data.performance) {
          performances[event] = data.performance;
        }
      } catch (error) {
        console.error(`Error calculating ${event}:`, error);
        performances[event] = 'N/A';
      }
    }

    setEquivalentPerformances(performances);
  };

  const getEventName = (event) => {
    const eventNames = {
      'DEC': 'Decathlon',
      'HEP': 'Heptathlon',
      'PEN': 'Pentathlon',
      '100m': '100m',
      '200m': '200m',
      '400m': '400m',
      '800m': '800m',
      '1500m': '1500m',
      '3000m': '3000m',
      '5000m': '5000m',
      '10000m': '10000m',
      '110mH': '110m Hurdles',
      '100mH': '100m Hurdles',
      '400mH': '400m Hurdles',
      '3000mSC': '3000m SC',
      '60m': '60m',
      '60mH': '60m Hurdles',
      'HJ': 'High Jump',
      'PV': 'Pole Vault',
      'LJ': 'Long Jump',
      'TJ': 'Triple Jump',
      'SP': 'Shot Put',
      'DT': 'Discus',
      'HT': 'Hammer',
      'JT': 'Javelin'
    };
    return eventNames[event] || event;
  };

  const renderEventSection = (title, eventList) => {
    const filteredEvents = eventList.filter(event => 
      equivalentPerformances[event] !== undefined && 
      equivalentPerformances[event] !== 'N/A'
    );

    if (filteredEvents.length === 0) return null;

    return (
      <div className="event-section">
        <h3>{title}</h3>
        <table>
          <tbody>
            {filteredEvents.map(event => (
              <tr key={event}>
                <td>{getEventName(event)}</td>
                <td>{equivalentPerformances[event]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  const getCombinedEvent = () => {
    if (season === 'outdoor') {
      return gender === 'mens' ? ['DEC'] : ['HEP'];
    } else {
      return gender === 'mens' ? ['HEP'] : ['PEN'];
    }
  };

  return (
    <div className="event-comparison">
      <h2>Required Performances for {points} Points</h2>
      {renderEventSection('Sprints & Hurdles', 
        season === 'indoor' 
          ? ['60m', '200m', '400m', '60mH']
          : ['100m', '200m', '400m', gender === 'mens' ? '110mH' : '100mH', '400mH']
      )}
      {renderEventSection('Middle Distance', ['800m', '1500m', '3000m'])}
      {season === 'outdoor' && renderEventSection('Long Distance', ['5000m', '10000m'])}
      {renderEventSection('Jumps', ['HJ', 'PV', 'LJ', 'TJ'])}
      {renderEventSection('Throws', 
        season === 'indoor' 
          ? ['SP']
          : ['SP', 'DT', 'HT', 'JT']
      )}
      {renderEventSection('Combined Events', getCombinedEvent())}
    </div>
  );
}

export default EventComparison;
