import { useState } from 'react';

function About() {
  const [openSection, setOpenSection] = useState(null);

  const toggleSection = (section) => {
    setOpenSection(openSection === section ? null : section);
  };

  return (
    <div className="about-page">
      <div className="accordion-section">
        <div className="section-header" onClick={() => toggleSection('calculator')}>
          <h2>Points Calculator ▾</h2>
          <p>The World Athletics Points Score is used to compare performances across different events...</p>
        </div>
        {openSection === 'calculator' && (
          <div className="section-content">
            <p>Enter a performance to calculate points, or enter points to find the required performance.</p>
            {/* Rest of calculator content */}
          </div>
        )}
      </div>

      <div className="accordion-section">
        <div className="section-header" onClick={() => toggleSection('competition')}>
          <h2>Competition Levels ▾</h2>
          <p>The World Athletics Ranking System includes both performance points and placing points...</p>
        </div>
        {openSection === 'competition' && (
          <div className="section-content">
            {/* Rest of competition content */}
          </div>
        )}
      </div>

      <div className="accordion-section">
        <div className="section-header" onClick={() => toggleSection('categories')}>
          <h2>Competition Categories ▾</h2>
          <p>Different competition levels award different placing points...</p>
        </div>
        {openSection === 'categories' && (
          <div className="section-content">
            <div className="competition-categories">
              {/* Categories content */}
            </div>
          </div>
        )}
      </div>

      <div className="accordion-section">
        <div className="section-header" onClick={() => toggleSection('results')}>
          <h2>Understanding Results ▾</h2>
          <p>When using the calculator, you might see "NaN" (Not a Number) in some cases...</p>
        </div>
        {openSection === 'results' && (
          <div className="section-content">
            {/* Results content */}
          </div>
        )}
      </div>
    </div>
  );
}

export default About;
