import { useState } from 'react';

function About() {
  const [visibleSections, setVisibleSections] = useState({
    calculator: false,
    competition: false,
    categories: false,
    results: false,
    wind: false
  });

  const toggleSection = (section) => {
    setVisibleSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  return (
    <div className="about-page">
      <h1>About World Athletics Points</h1>

      <div className="about-buttons">
        <button onClick={() => toggleSection('calculator')}>
          Points Calculator
        </button>
        <button onClick={() => toggleSection('competition')}>
          Competition Levels
        </button>
        <button onClick={() => toggleSection('categories')}>
          Competition Categories
        </button>
        <button onClick={() => toggleSection('results')}>
          Understanding Results
        </button>
        <button onClick={() => toggleSection('wind')}>
          Wind Modifications
        </button>
      </div>

      {visibleSections.calculator && (
        <section className="about-section">
          <h2>Points Calculator</h2>
          {/* Existing calculator content */}
        </section>
      )}

      {visibleSections.competition && (
        <section className="about-section">
          <h2>Competition Levels</h2>
          {/* Existing competition levels content */}
        </section>
      )}

      {visibleSections.categories && (
        <section className="about-section">
          <h2>Competition Categories</h2>
          {/* Existing categories content */}
        </section>
      )}

      {visibleSections.results && (
        <section className="about-section">
          <h2>Understanding Results</h2>
          {/* Existing results content */}
        </section>
      )}

      {visibleSections.wind && (
        <section className="about-section">
          <h2>Wind Modifications</h2>
          {/* Existing wind content */}
        </section>
      )}
    </div>
  );
}

export default About;
