import React, { useState, useEffect } from 'react';
import { formatPerformance } from '../utils/formatters';

const EVENT_CODES = {
  '60m': '60m',
  '100m': '100m',
  '200m': '200m',
  '400m': '400m',
  '800m': '800m',
  '1500m': '1500m',
  '3000m': '3000m',
  '5000m': '5000m',
  '10000m': '10000m',
  '60mH': '60mH',
  '100mH': '100mH',
  '110mH': '110mH',
  '400mH': '400mH',
  'High Jump': 'HJ',
  'Pole Vault': 'PV',
  'Long Jump': 'LJ',
  'Triple Jump': 'TJ',
  'Shot Put': 'SP',
  'Discus Throw': 'DT',
  'Hammer Throw': 'HT',
  'Javelin Throw': 'JT',
  'Decathlon': 'Decathlon',
  'Heptathlon': 'Heptathlon',
  'Pentathlon': 'Pentathlon'
};

function EventComparison({ points, gender, season }) {
  const [performances, setPerformances] = useState({});

  const getEventsByGenderAndSeason = () => {
    if (season === 'indoor') {
      return [
        '60m',
        '200m',
        '400m',
        '800m',
        '1500m',
        '3000m',
        gender === 'mens' ? '60mH' : '60mH',
        'High Jump',
        'Pole Vault',
        'Long Jump',
        'Triple Jump',
        'Shot Put'
      ];
    }
    return [
      '100m',
      '200m',
      '400m',
      '800m',
      '1500m',
      '3000m',
      '5000m',
      '10000m',
      gender === 'mens' ? '110mH' : '100mH',
      '400mH',
      'High Jump',
      'Pole Vault',
      'Long Jump',
      'Triple Jump',
      'Shot Put',
      'Discus Throw',
      'Hammer Throw',
      'Javelin Throw'
    ];
  };

  const calculateEquivalentPerformances = () => {
    if (!points) return;

    const events = getEventsByGenderAndSeason();
    const promises = events.map(event =>
      fetch(`/api/performance/${gender}/${season}/${event}/${points}`)
        .then(response => response.json())
        .then(data => ({ event, performance: data.performance }))
        .catch(error => {
          console.error('Error:', error);
          return { event, performance: null };
        })
    );

    Promise.all(promises).then(results => {
      const performanceMap = {};
      results.forEach(({ event, performance }) => {
        performanceMap[event] = performance;
      });
      setPerformances(performanceMap);
    });
  };

  useEffect(() => {
    if (points) {
      calculateEquivalentPerformances();
    }
  }, [points, gender, season, calculateEquivalentPerformances, getEventsByGenderAndSeason]);

  if (!points || Object.keys(performances).length === 0) return null;

  return (
    <div className="event-comparison">
      <h2>Equivalent Performances</h2>
      <table>
        <thead>
          <tr>
            <th>Event</th>
            <th>Performance</th>
          </tr>
        </thead>
        <tbody>
          {getEventsByGenderAndSeason().map(event => (
            <tr key={event}>
              <td>{event}</td>
              <td>{formatPerformance(performances[event], event)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EventComparison;
